class App extends React.Component {
  modes = ['a', 'b', 'c'];

  constructor(props) {
      super(props);
      this.state = {
          currentMode: this.modes[2],
          register: any
      };
  };

  setMode = (newMode) => {
      console.log('App.setMode', this.state.currentMode, newMode);
      this.setState({currentMode: newMode});
  };

  nextMode = (event) => {
      const index = this.modes.indexOf(this.state.currentMode);
      const newMode = this.modes[(index+1) % this.modes.length];
      console.log('App.nextMode', this.state.currentMode, newMode);
      this.setState({currentMode: newMode});
  };

  render() {
      return (
          <div>
            <SelectMode
              modes={this.modes}
              currentMode={this.state.currentMode}
              handleChange={this.setMode}
              register={this.state.register}
            />
            <Button
              onClick={this.nextMode}>
              Next mode
            </Button>
          </div>
      );
  };
}

const SelectMode = (props) => {
  console.log('SelectMode', props);
  return (
      <Form>
        <Form.Group controlId="testSelect">
        <Form.Label>Test Select</Form.Label>
        <Form.Control
          as="select"
          onChange={event => props.handleChange(event.target.value)}
          ref={props.register}
          defaultValue={props.currentMode}>
          {props.modes.map(m => <option key={m} value={m}>{m}</option>)}
        </Form.Control>
        </Form.Group>
        <Form.Group controlId="choiceDisplay">
          <Form.Label>Current Choice</Form.Label>
          <Form.Control
            type='text'
            onChange={event => props.handleChange(event.target.value)}
            defaultValue={props.currentMode}
            ref={props.register}
            readOnly>
          </Form.Control>
        </Form.Group>
      </Form>);

};

export default App;
